import React from "react";
import { NavLink } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { useProducts } from "../hooks/useProducts";
import "./NavBar.css";

function Navigation({ filters, setFilters }) {
  const { getTotalItems } = useCart();
  const { categories, loading } = useProducts();

  // Use default categories while loading
  const displayCategories = loading ? ["All"] : categories;

  return (
    <nav style={navStyle}>
      <div style={navContainerStyle}>
        <div style={sectionLeft}>
          <ul style={navListStyle}>
            <li>
              <NavLink
                to="/"
                style={({ isActive }) =>
                  isActive ? activeLinkStyle : linkStyle
                }
              >
                <img src="/favicon.svg" alt="logo" style={logo} />
                <span style={logoText}>UmaStore</span>
              </NavLink>
            </li>
          </ul>
        </div>

        <div style={sectionCenter}>
          <div style={filterBarStyle}>
            <input
              type="text"
              value={filters.search}
              onChange={(e) =>
                setFilters({ ...filters, search: e.target.value })
              }
              placeholder="Search"
              style={filterStyle}
            />
            <select
              value={filters.category}
              onChange={(e) =>
                setFilters({ ...filters, category: e.target.value })
              }
              style={filterStyle}
            >
              {displayCategories.map((c) => (
                <option key={c}>{c}</option>
              ))}
            </select>
            <input
              type="number"
              placeholder="Max Price"
              value={filters.maxPrice}
              onChange={(e) =>
                setFilters({ ...filters, maxPrice: e.target.value })
              }
              style={filterStyle}
            />
          </div>
        </div>

        <div style={sectionRight}>
          <ul style={navListStyle}>
            <li>
              <NavLink
                to="/cart"
                style={({ isActive }) =>
                  isActive ? activeLinkStyle : linkStyle
                }
              >
                Cart ({getTotalItems()})
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/checkout"
                style={({ isActive }) =>
                  isActive ? activeLinkStyle : linkStyle
                }
              >
                Checkout
              </NavLink>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Navigation;
