import React from "react";
import { useParams } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { useProduct } from "../hooks/useProducts";

const containerStyle = {
  padding: "2rem",
  maxWidth: "600px",
  margin: "0 auto",
  display: "flex",
  flexDirection: "row",
};

const imageStyle = {
  width: "100%",
  borderRadius: "8px",
  objectFit: "contain",
  maxHeight: "400px",
};

const titleStyle = {
  marginTop: "1rem",
};

const categoryStyle = {
  color: "#888",
  textTransform: "capitalize",
};

const priceStyle = {
  fontWeight: "bold",
};

const buttonStyle = {
  padding: "0.75rem 1.5rem",
  background: "#6ABA00",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
};

const notFoundStyle = {
  padding: "2rem",
  textAlign: "center",
};

const leftSection = {
  flex: "1",
  paddingRight: "1rem",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
};

const rightSection = {
  flex: "1",
  paddingLeft: "1rem",
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
};

const loadingStyle = {
  padding: "2rem",
  textAlign: "center",
};

const errorStyle = {
  padding: "2rem",
  textAlign: "center",
  color: "red",
};

function ProductDetailsPage() {
  const { id } = useParams();
  const { addToCart } = useCart();
  const { product, loading, error } = useProduct(id);

  if (loading) {
    return <div style={loadingStyle}>Loading product...</div>;
  }

  if (error) {
    return <div style={errorStyle}>Error loading product: {error}</div>;
  }

  if (!product) {
    return <p style={notFoundStyle}>Product not found.</p>;
  }

  return (
    <div style={containerStyle}>
      <div style={leftSection}>
        <img src={product.image} alt={product.name} style={imageStyle} />
      </div>

      <div style={rightSection}>
        <h2 style={titleStyle}>{product.name}</h2>
        <p style={categoryStyle}>{product.category}</p>
        <p style={priceStyle}>${product.price}</p>
        <p>{product.description}</p>
        {product.rating && (
          <p>
            Rating: {product.rating.rate}/5 ({product.rating.count} reviews)
          </p>
        )}
        <button style={buttonStyle} onClick={() => addToCart(product)}>
          Add to Cart
        </button>
      </div>
    </div>
  );
}

export default ProductDetailsPage;
