import React from 'react';
import { NavLink } from 'react-router-dom';
import { useCart } from '../context/CartContext';

function Navigation() {
  const { getTotalItems } = useCart();

  const linkStyle = {
    color: 'white',
    textDecoration: 'none',
    padding: '0.5rem 1rem',
    borderRadius: '4px'
  };

  const navStyle = {
    backgroundColor: '#81CE03',
    padding: '1rem',
    marginBottom: '2rem'
  };

  const navListStyle = {
    display: 'flex',
    gap: '2rem',
    listStyle: 'none',
    margin: 0,
    padding: 0
  };

  return (
    <nav style={navStyle}>
      <ul style={navListStyle}>
        <li>
          <NavLink to="/" style={({ isActive }) => isActive ? { ...linkStyle, backgroundColor: '#6ABA00' } : linkStyle}>
            Products
          </NavLink>
        </li>
        <li>
          <NavLink to="/cart" style={({ isActive }) => isActive ? { ...linkStyle, backgroundColor: '#3498db' } : linkStyle}>
            Cart ({getTotalItems()})
          </NavLink>
        </li>
        <li>
          <NavLink to="/checkout" style={({ isActive }) => isActive ? { ...linkStyle, backgroundColor: '#3498db' } : linkStyle}>
            Checkout
          </NavLink>
        </li>
      </ul>
    </nav>
  );
}

export default Navigation;
