import React from "react";
import { useCart } from "../context/CartContext";
import { Link } from "react-router-dom";

const mockProducts = [
  {
    id: 1,
    name: "Dirt racing shoe",
    price: 90,
    category: "Shoes",
    description: "Shoes fitting for racing on dirt tracks.",
    image:
      "/dirtShoe.png",
  },
  {
    id: 2,
    name: "Mile racing shoe",
    price: 599,
    category: "Shoes",
    description: "Shoes suitable for racing mile distances.",
    image:
      "/mileShoe.png",
  },
  {
    id: 3,
    name: "Medium racing shoe",
    price: 29,
    category: "Shoes",
    description: "Shoe built for medium length tracks.",
    image:
      "/mediumShoe.png",
  },
  {
    id: 4,
    name: "Long racing shoe",
    price: 79,
    category: "Shoes",
    description: "Shoes made for long distance running",
    image:
      "/longShoe.png",
  },
  {
    id: 5,
    name: "Book",
    price: 19,
    category: "Books",
    description: "Informative book.",
    image:
      "https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=300&h=200&fit=crop",
  },
  {
    id: 6,
    name: "Headphones",
    price: 149,
    category: "Electronics",
    description: "Noise-cancelling.",
    image:
      "https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=300&h=200&fit=crop",
  },
];

const containerStyle = {
  padding: "0 2rem",
};

const titleStyle = {
  textAlign: "center",
};

const gridStyle = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
  gap: "1.5rem",
};

const cardStyle = {
  border: "1px solid #ccc",
  padding: "1rem",
  borderRadius: "6px",
  background: "white",
};

const linkStyle = {
  textDecoration: "none",
  color: "inherit",
};

const imageStyle = {
  width: "75%",
  height: "180px",
  objectFit: "cover",
  paddingLeft: '30px'
};

const buttonStyle = {
  padding: "0.75rem 1.5rem",
  background: "#3498db",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
};

function HomePage({ filters }) {
  const { addToCart } = useCart();

  const filtered = mockProducts.filter((p) => {
    const matchSearch = p.name
      .toLowerCase()
      .includes(filters.search.toLowerCase());
    const matchCat =
      filters.category === "All" || p.category === filters.category;
    const matchPrice =
      !filters.maxPrice || p.price <= parseFloat(filters.maxPrice);
    return matchSearch && matchCat && matchPrice;
  });

  return (
    <div style={containerStyle}>
      <h1 style={titleStyle}>Product Catalog</h1>
      <div style={gridStyle}>
        {filtered.map((product) => (
          <div key={product.id} style={cardStyle}>
            <Link to={`/product/${product.id}`} style={linkStyle}>
              <img src={product.image} alt={product.name} style={imageStyle} />
              <h3>{product.name}</h3>
              <p>{product.category}</p>
              <p>${product.price}</p>
            </Link>
            <button style={buttonStyle} onClick={() => addToCart(product)}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
