import React from "react";
import { useCart } from "../context/CartContext";
import { Link } from "react-router-dom";
import { useProducts } from "../hooks/useProducts";

const containerStyle = {
  padding: "0 2rem",
};

const titleStyle = {
  textAlign: "center",
};

const gridStyle = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
  gap: "1.5rem",
};

const cardStyle = {
  border: "1px solid #ccc",
  padding: "1rem",
  borderRadius: "6px",
  background: "white",
};

const linkStyle = {
  textDecoration: "none",
  color: "inherit",
};

const imageStyle = {
  width: "100%",
  height: "180px",
  objectFit: "contain",
  padding: "10px",
};

const buttonStyle = {
  padding: "0.75rem 1.5rem",
  background: "#6ABA00",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
};

function HomePage({ filters }) {
  const { addToCart } = useCart();
  const { products, loading, error } = useProducts();

  // Show loading state
  if (loading) {
    return (
      <div style={containerStyle}>
        <h1 style={titleStyle}>Product Catalog</h1>
        <div style={{ textAlign: "center", padding: "2rem" }}>
          <p>Loading products...</p>
        </div>
      </div>
    );
  }

  // Show error state
  if (error) {
    return (
      <div style={containerStyle}>
        <h1 style={titleStyle}>Product Catalog</h1>
        <div style={{ textAlign: "center", padding: "2rem", color: "red" }}>
          <p>Error loading products: {error}</p>
        </div>
      </div>
    );
  }

  const filtered = products.filter((p) => {
    const matchSearch = p.name
      .toLowerCase()
      .includes(filters.search.toLowerCase());
    const matchCat =
      filters.category === "All" ||
      p.category.toLowerCase() === filters.category.toLowerCase();
    const matchPrice =
      !filters.maxPrice || p.price <= parseFloat(filters.maxPrice);
    return matchSearch && matchCat && matchPrice;
  });

  return (
    <div style={containerStyle}>
      <h1 style={titleStyle}>Product Catalog</h1>
      <div style={gridStyle}>
        {filtered.map((product) => (
          <div key={product.id} style={cardStyle}>
            <Link to={`/product/${product.id}`} style={linkStyle}>
              <img src={product.image} alt={product.name} style={imageStyle} />
              <h3>{product.name}</h3>
              <p>{product.category}</p>
              <p>${product.price}</p>
            </Link>
            <button style={buttonStyle} onClick={() => addToCart(product)}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
