import React from "react";
import { NavLink } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { useProducts } from "../hooks/useProducts";

const navStyle = {
  backgroundColor: "#81CE03",
  padding: "1rem",
  marginBottom: "2rem",
  display: "flex",
  flexDirection: "row",
  alignItems: "center",
  gap: "4rem",
};

const navListStyle = {
  display: "flex",
  alignItems: "center",
  gap: "2rem",
  listStyle: "none",
  margin: 0,
  padding: 0,
};

const linkStyle = {
  color: "white",
  textDecoration: "none",
  padding: "0.5rem 1rem",
  borderRadius: "4px",
  display: "flex",
  alignItems: "center",
  gap: "0.5rem",
};

const activeLinkStyle = {
  ...linkStyle,
  backgroundColor: "#6ABA00",
};

const logo = {
  width: "40px",
  height: "auto",
};

const logoText = {
  margin: 0,
  fontSize: "1.2rem",
  fontWeight: "bold",
};

const navContainerStyle = {
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
  width: "100%",
};

const sectionLeft = {
  display: "flex",
  alignItems: "center",
  gap: "1rem",
  flex: "1",
};

const sectionCenter = {
  display: "flex",
  justifyContent: "center",
  flex: "2",
};

const sectionRight = {
  display: "flex",
  justifyContent: "flex-end",
  gap: "2rem",
  flex: "1",
};

const filterBarStyle = {
  display: "flex",
  alignItems: "center",
  gap: "1rem",
};

const filterStyle = {
  height: "35px",
  padding: "0.4rem 0.6rem",
  border: "1px solid #ccc",
  borderRadius: "4px",
  outline: "none",
  fontSize: "0.9rem",
  fontFamily: "inherit",
  backgroundColor: "#fff",
  color: "#333",
  boxShadow: "0 1px 2px rgba(0,0,0,0.1)",
  appearance: "none",
};

function Navigation({ filters, setFilters }) {
  const { getTotalItems } = useCart();
  const { categories, loading } = useProducts();

  // Use default categories while loading
  const displayCategories = loading ? ["All"] : categories;

  return (
    <nav style={navStyle}>
      <div style={navContainerStyle}>
        <div style={sectionLeft}>
          <ul style={navListStyle}>
            <li>
              <NavLink
                to="/"
                style={({ isActive }) =>
                  isActive ? activeLinkStyle : linkStyle
                }
              >
                <img src="/favicon.svg" alt="logo" style={logo} />
                <span style={logoText}>UmaStore</span>
              </NavLink>
            </li>
          </ul>
        </div>

        <div style={sectionCenter}>
          <div style={filterBarStyle}>
            <input
              type="text"
              value={filters.search}
              onChange={(e) =>
                setFilters({ ...filters, search: e.target.value })
              }
              placeholder="Search"
              style={filterStyle}
            />
            <select
              value={filters.category}
              onChange={(e) =>
                setFilters({ ...filters, category: e.target.value })
              }
              style={filterStyle}
            >
              {categories.map((c) => (
                <option key={c}>{c}</option>
              ))}
            </select>
            <input
              type="number"
              placeholder="Max Price"
              value={filters.maxPrice}
              onChange={(e) =>
                setFilters({ ...filters, maxPrice: e.target.value })
              }
              style={filterStyle}
            />
          </div>
        </div>

        <div style={sectionRight}>
          <ul style={navListStyle}>
            <li>
              <NavLink
                to="/cart"
                style={({ isActive }) =>
                  isActive ? activeLinkStyle : linkStyle
                }
              >
                Cart ({getTotalItems()})
              </NavLink>
            </li>
            <li>
              <NavLink
                to="/checkout"
                style={({ isActive }) =>
                  isActive ? activeLinkStyle : linkStyle
                }
              >
                Checkout
              </NavLink>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Navigation;
