import React, { useState } from "react";
import { useCart } from "../context/CartContext";
import "./checkout.css";

function CheckoutPage() {
  const { cart, getTotalPrice, clearCart } = useCart();
  const [placed, setPlaced] = useState(false);

  const handlePlaceOrder = () => {
    setPlaced(true);
    clearCart();
  };

  if (cart.length === 0 && !placed) {
    return <p style={centeredTextStyle}>Cart is empty.</p>;
  }

  if (placed) {
    return <p style={successTextStyle}>Order placed! Thank you.</p>;
  }

  return (
    <div style={containerStyle}>
      <h1>Checkout</h1>

      {cart.map((item) => (
        <div key={item.id} style={itemStyle}>
          <span>
            {item.name} x {item.quantity}
          </span>
          <span>${(item.price * item.quantity).toFixed(2)}</span>
        </div>
      ))}

      <h3 style={totalStyle}>Total: ${getTotalPrice().toFixed(2)}</h3>

      <button onClick={handlePlaceOrder} style={buttonStyle}>
        Place Order
      </button>
    </div>
  );
}

export default CheckoutPage;
