import React from "react";
import { useParams } from "react-router-dom";
import { useCart } from "../context/CartContext";

const mockProducts = [
  {
    id: 1,
    name: "Laptop",
    price: 999,
    category: "Electronics",
    description: "High-performance laptop for all your needs.",
    image:
      "https://images.unsplash.com/photo-1496181133206-80ce9b88a853?w=300&h=200&fit=crop",
  },
  {
    id: 2,
    name: "Phone",
    price: 599,
    category: "Electronics",
    description: "Stay connected with the latest smartphone.",
    image:
      "https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?w=300&h=200&fit=crop",
  },
  {
    id: 3,
    name: "T-Shirt",
    price: 29,
    category: "Clothing",
    description: "Comfortable cotton t-shirt.",
    image:
      "https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?w=300&h=200&fit=crop",
  },
  {
    id: 4,
    name: "Jeans",
    price: 79,
    category: "Clothing",
    description: "Stylish denim jeans.",
    image:
      "https://images.unsplash.com/photo-1542272604-787c3835535d?w=300&h=200&fit=crop",
  },
  {
    id: 5,
    name: "Book",
    price: 19,
    category: "Books",
    description: "Engaging and informative book.",
    image:
      "https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=300&h=200&fit=crop",
  },
  {
    id: 6,
    name: "Headphones",
    price: 149,
    category: "Electronics",
    description: "Noise-cancelling over-ear headphones.",
    image:
      "https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=300&h=200&fit=crop",
  },
];

const containerStyle = {
  padding: "2rem",
  maxWidth: "600px",
  margin: "0 auto",
};

const imageStyle = {
  width: "100%",
  borderRadius: "8px",
};

const titleStyle = {
  marginTop: "1rem",
};

const categoryStyle = {
  color: "#888",
};

const priceStyle = {
  fontWeight: "bold",
};

const buttonStyle = {
  padding: "0.75rem 1.5rem",
  background: "#3498db",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
};

const notFoundStyle = {
  padding: "2rem",
  textAlign: "center",
};

function ProductDetailsPage() {
  const { id } = useParams();
  const { addToCart } = useCart();
  const product = mockProducts.find((p) => p.id === parseInt(id));

  if (!product) {
    return <p style={notFoundStyle}>Product not found.</p>;
  }

  return (
    <div style={containerStyle}>
      <img src={product.image} alt={product.name} style={imageStyle} />
      <h2 style={titleStyle}>{product.name}</h2>
      <p style={categoryStyle}>{product.category}</p>
      <p style={priceStyle}>${product.price}</p>
      <p>{product.description}</p>
      <button style={buttonStyle} onClick={() => addToCart(product)}>
        Add to Cart
      </button>
    </div>
  );
}

export default ProductDetailsPage;
