import React from 'react';
import { useParams } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import mockProducts from '../data/products';

function ProductDetailsPage() {
  const { id } = useParams();
  const { addToCart } = useCart();
  const product = mockProducts.find(p => p.id === parseInt(id));

  if (!product) {
    return <p style={{ padding: '2rem', textAlign: 'center' }}>Product not found.</p>;
  }

  return (
    <div style={{ padding: '2rem', maxWidth: '600px', margin: '0 auto' }}>
      <img src={product.image} alt={product.name} style={{ width: '100%', borderRadius: '8px' }} />
      <h2 style={{ marginTop: '1rem' }}>{product.name}</h2>
      <p style={{ color: '#888' }}>{product.category}</p>
      <p style={{ fontWeight: 'bold' }}>${product.price}</p>
      <p>{product.description}</p>
      <button
        style={{ padding: '0.75rem 1.5rem', background: '#3498db', color: 'white', border: 'none', borderRadius: '4px' }}
        onClick={() => addToCart(product)}
      >
        Add to Cart
      </button>
    </div>
  );
}

export default ProductDetailsPage;
