import React, { useState } from "react";
import { useCart } from "../context/CartContext";

const containerStyle = {
  padding: "2rem",
  maxWidth: "600px",
  margin: "auto",
};

const centeredTextStyle = {
  textAlign: "center",
  marginTop: "2rem",
};

const successTextStyle = {
  ...centeredTextStyle,
  color: "#27ae60",
};

const itemStyle = {
  display: "flex",
  justifyContent: "space-between",
  padding: "0.5rem 0",
  borderBottom: "1px solid #eee",
};

const totalStyle = {
  marginTop: "1rem",
  textAlign: "right",
};

const buttonStyle = {
  width: "100%",
  padding: "1rem",
  marginTop: "1rem",
  background: "#27ae60",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
};

function CheckoutPage() {
  const { cart, getTotalPrice, removeFromCart } = useCart();
  const [placed, setPlaced] = useState(false);

  if (cart.length === 0) {
    return <p style={centeredTextStyle}>Cart is empty.</p>;
  }

  if (placed) {
    return <p style={successTextStyle}>Order placed! Thank you.</p>;
  }

  return (
    <div style={containerStyle}>
      <h1>Checkout</h1>

      {cart.map((item) => (
        <div key={item.id} style={itemStyle}>
          <span>
            {item.name} x {item.quantity}
          </span>
          <span>${(item.price * item.quantity).toFixed(2)}</span>
        </div>
      ))}

      <h3 style={totalStyle}>Total: ${getTotalPrice().toFixed(2)}</h3>

      <button onClick={() => {
        setPlaced(true)
        cart.map(item)
        removeFromCart(item.id)
        }} style={buttonStyle}> 
        Place Order
      </button>
    </div>
  );
}

export default CheckoutPage;
