import React from 'react';
import { NavLink } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const navStyle = {
  backgroundColor: '#81CE03',
  padding: '1rem',
  marginBottom: '2rem'
};

const navListStyle = {
  display: 'flex',
  textAlign: 'center',
  margin: 'auto',
  gap: '4rem',
  listStyle: 'none',
  padding: 0,
};

const linkStyle = {
  color: 'white',
  textDecoration: 'none',
  padding: '0.5rem 1rem',
  borderRadius: '4px'
};

const activeLinkStyle = {
  ...linkStyle,
  backgroundColor: '#6ABA00'
};

const logo = {
  width: '70px',
  height: 'auto',
};

const LogoBtn = {
  display: 'flex',
  flexDirection: 'row',
}

function Navigation() {
  const { getTotalItems } = useCart();

  return (
    <nav style={navStyle}>
      <ul style={navListStyle}>
        <li>
          <NavLink
            to="/"
            style={({ isActive }) => isActive ? activeLinkStyle : linkStyle}
          >
            <div style={LogoBtn}>
              <img 
              src="/favicon.svg" 
              alt="logo"
              style={logo}
              />
              <p>
                UmaStore
              </p>
            </div>

          </NavLink>
        </li>
        <li>
          <NavLink
            to="/cart"
            style={({ isActive }) => isActive ? activeLinkStyle : linkStyle}
          >
            Cart ({getTotalItems()})
          </NavLink>
        </li>
        <li>
          <NavLink
            to="/checkout"
            style={({ isActive }) => isActive ? activeLinkStyle : linkStyle}
          >
            Checkout
          </NavLink>
        </li>
      </ul>
    </nav>
  );
}

export default Navigation;
