import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { useCart } from '../context/CartContext';

// Style constants
const navStyle = {
  backgroundColor: '#81CE03',
  padding: '1rem',
  marginBottom: '2rem'
};

const navListStyle = {
  display: 'flex',
  alignItems: 'center',
  gap: '4rem',
  listStyle: 'none',
  margin: 0,
  padding: 0
};

const linkStyle = {
  color: 'white',
  textDecoration: 'none',
  padding: '0.5rem 1rem',
  borderRadius: '4px',
  display: 'flex',
  alignItems: 'center',
  gap: '0.5rem'
};

const activeLinkStyle = {
  ...linkStyle,
  backgroundColor: '#6ABA00'
};

const logo = {
  width: '40px',
  height: 'auto',
};

const logoText = {
  margin: 0,
  fontSize: '1.2rem',
  fontWeight: 'bold'
};

const filterBarStyle = {
  marginTop: '1rem',
  display: 'flex',
  gap: '1rem'
};

function Navigation({ filters, setFilters }) {
  const { getTotalItems } = useCart();
  const categories = ['All', 'Electronics', 'Clothing', 'Books'];

  return (
    <nav style={navStyle}>
      <ul style={navListStyle}>
        <li>
          <NavLink to="/" style={({ isActive }) => isActive ? activeLinkStyle : linkStyle}>
            <img src="/favicon.svg" alt="logo" style={logo} />
            <span style={logoText}>UmaStore</span>
          </NavLink>
        </li>
        <li>
          <NavLink to="/cart" style={({ isActive }) => isActive ? activeLinkStyle : linkStyle}>
            Cart ({getTotalItems()})
          </NavLink>
        </li>
        <li>
          <NavLink to="/checkout" style={({ isActive }) => isActive ? activeLinkStyle : linkStyle}>
            Checkout
          </NavLink>
        </li>
      </ul>

      {/* Filter and Search Bar */}
      <div style={filterBarStyle}>
        <input
          type="text"
          value={filters.search}
          onChange={e => setFilters({ ...filters, search: e.target.value })}
          placeholder="Search"
        />
        <select
          value={filters.category}
          onChange={e => setFilters({ ...filters, category: e.target.value })}
        >
          {categories.map(c => <option key={c}>{c}</option>)}
        </select>
        <input
          type="number"
          placeholder="Max Price"
          value={filters.maxPrice}
          onChange={e => setFilters({ ...filters, maxPrice: e.target.value })}
        />
      </div>
    </nav>
  );
}

export default Navigation;
