import React from 'react';
import { NavLink } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import logo from "../../public/favicon.svg"

const navStyle = {
  backgroundColor: '#81CE03',
  padding: '1rem',
  marginBottom: '2rem'
};

const navListStyle = {
  display: 'flex',
  gap: '2rem',
  listStyle: 'none',
  margin: 0,
  padding: 0
};

const linkStyle = {
  color: 'white',
  textDecoration: 'none',
  padding: '0.5rem 1rem',
  borderRadius: '4px'
};

const activeLinkStyle = {
  ...linkStyle,
  backgroundColor: '#6ABA00'
};

function Navigation() {
  const { getTotalItems } = useCart();

  return (
    <nav style={navStyle}>
      <ul style={navListStyle}>
        <li>
          <NavLink
            to="/"
            style={({ isActive }) => isActive ? activeLinkStyle : linkStyle}
          >
            <img src="../public/favicon.svg" alt="bruh"/>
          </NavLink>
        </li>
        <li>
          <NavLink
            to="/cart"
            style={({ isActive }) => isActive ? activeLinkStyle : linkStyle}
          >
            Cart ({getTotalItems()})
          </NavLink>
        </li>
        <li>
          <NavLink
            to="/checkout"
            style={({ isActive }) => isActive ? activeLinkStyle : linkStyle}
          >
            Checkout
          </NavLink>
        </li>
      </ul>
    </nav>
  );
}

export default Navigation;
