import React from "react";
import { useCart } from "../context/CartContext";
import { Link } from "react-router-dom";

const mockProducts = [
  {
    id: 1,
    name: "Dirt racing shoes",
    price: 90,
    category: "Shoes",
    description: "Brand-name shoes that keep clean in the sloppiest of dirt. Used to raise Potential Level.",
    image:
      "/dirtShoe.png",
  },
  {
    id: 2,
    name: "Mile racing shoes",
    price: 90,
    category: "Shoes",
    description: "A besteller that revolutionized the mile shoe industry. Used to raise Potential Level.",
    image:
      "/mileShoe.png",
  },
  {
    id: 3,
    name: "Medium racing shoes",
    price: 90,
    category: "Shoes",
    description: "The medium-distance shoes you can always trust. Used to raise Potential Level.",
    image:
      "/mediumShoe.png",
  },
  {
    id: 4,
    name: "Long racing shoes",
    price: 100,
    category: "Shoes",
    description: "Shoes from the Light Weight series that'll keep you light on your feet over long distances.",
    image:
      "/longShoe.png",
  },
  {
    id: 5,
    name: "Sprint racing shoes",
    price: 100,
    category: "Shoes",
    description: "The hottest new shore from a popular sprinting brand. Used to raise Potential Level.",
    image:
      "/sprintShoe.png",
  },
  {
    id: 6,
    name: "Carrot jelly mini",
    price: 19,
    category: "Food",
    description: "A jolt of energy that lasts for hours! Restores 1 RP.",
    image:
      "/carrotJelly.png",
  },
  {
    id: 7,
    name: "Juice",
    price: 49,
    category: "Food",
    description: "Drink this for revitalization! Restores 30 TP.",
    image:
      "/tp.png",
  },
  {
    id: 8,
    name: "Pleasing parfait",
    price: 29,
    category: "Food",
    description: "Raises a runner's mood to Great. Can be used in modes such as Team Trials.",
    image:
      "/parfait.png",
  },
  {
    id: 9,
    name: "Trainee scout ticket",
    price: 99,
    category: "Ticket",
    description: "Used for scouting Trainees. Can be used from the scout list.",
    image:
      "/traineeTicket.png",
  },
  {
    id: 10,
    name: "Support card scout ticket",
    price: 129,
    category: "Ticket",
    description: "Used for scouting Support Cards. Can be used from the scout list.",
    image:
      "/supportTicket.png",
  },
  {
    id: 11,
    name: "Silver cleat",
    price: 2,
    category: "",
    description: "",
    image:
      "/",
  },

];

const containerStyle = {
  padding: "0 2rem",
};

const titleStyle = {
  textAlign: "center",
};

const gridStyle = {
  display: "grid",
  gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
  gap: "1.5rem",
};

const cardStyle = {
  border: "1px solid #ccc",
  padding: "1rem",
  borderRadius: "6px",
  background: "white",
};

const linkStyle = {
  textDecoration: "none",
  color: "inherit",
};

const imageStyle = {
  width: "75%",
  height: "180px",
  objectFit: "cover",
  paddingLeft: '30px'
};

const buttonStyle = {
  padding: "0.75rem 1.5rem",
  background: "#3498db",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
};

function HomePage({ filters }) {
  const { addToCart } = useCart();

  const filtered = mockProducts.filter((p) => {
    const matchSearch = p.name
      .toLowerCase()
      .includes(filters.search.toLowerCase());
    const matchCat =
      filters.category === "All" || p.category === filters.category;
    const matchPrice =
      !filters.maxPrice || p.price <= parseFloat(filters.maxPrice);
    return matchSearch && matchCat && matchPrice;
  });

  return (
    <div style={containerStyle}>
      <h1 style={titleStyle}>Product Catalog</h1>
      <div style={gridStyle}>
        {filtered.map((product) => (
          <div key={product.id} style={cardStyle}>
            <Link to={`/product/${product.id}`} style={linkStyle}>
              <img src={product.image} alt={product.name} style={imageStyle} />
              <h3>{product.name}</h3>
              <p>{product.category}</p>
              <p>${product.price}</p>
            </Link>
            <button style={buttonStyle} onClick={() => addToCart(product)}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
