import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

function CheckoutPage() {
  const { cart, getTotalPrice } = useCart();
  const [placed, setPlaced] = useState(false);

  if (cart.length === 0) {
    return <p style={{ textAlign: 'center', marginTop: '2rem' }}>Cart is empty.</p>;
  }

  if (placed) {
    return <p style={{ textAlign: 'center', marginTop: '2rem', color: '#27ae60' }}>Order placed! Thank you.</p>;
  }

  return (
    <div style={{ padding: '2rem', maxWidth: '600px', margin: 'auto' }}>
      <h1>Checkout</h1>
      {cart.map(item => (
        <div key={item.id} style={{ display: 'flex', justifyContent: 'space-between' }}>
          <span>{item.name} x {item.quantity}</span>
          <span>${(item.price * item.quantity).toFixed(2)}</span>
        </div>
      ))}
      <hr />
      <h3>Total: ${getTotalPrice().toFixed(2)}</h3>
      <button onClick={() => setPlaced(true)} style={{
        width: '100%', padding: '1rem', marginTop: '1rem',
        background: '#27ae60', color: 'white', border: 'none', borderRadius: '4px'
      }}>
        Place Order
      </button>
    </div>
  );
}

export default CheckoutPage;
