import React from 'react';
import { useCart } from '../context/CartContext';

function Navigation({ currentPage, setCurrentPage }) {
  const { getTotalItems } = useCart();

  const linkStyle = {
    color: 'white',
    textDecoration: 'none',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    cursor: 'pointer'
  };

  const activeLinkStyle = {
    ...linkStyle,
    backgroundColor: '#3498db'
  };

  return (
    <nav style={{ backgroundColor: '#2c3e50', padding: '1rem', marginBottom: '2rem' }}>
      <ul style={{ display: 'flex', listStyle: 'none', gap: '2rem', margin: 0, padding: 0 }}>
        <li>
          <span
            style={currentPage === 'products' ? activeLinkStyle : linkStyle}
            onClick={() => setCurrentPage('products')}>
            Products
          </span>
        </li>
        <li>
          <span
            style={currentPage === 'cart' ? activeLinkStyle : linkStyle}
            onClick={() => setCurrentPage('cart')}>
            Cart ({getTotalItems()})
          </span>
        </li>
        <li>
          <span
            style={currentPage === 'checkout' ? activeLinkStyle : linkStyle}
            onClick={() => setCurrentPage('checkout')}>
            Checkout
          </span>
        </li>
      </ul>
    </nav>
  );
}

export default Navigation;
