import React from 'react';
import { useCart } from '../context/CartContext';

const containerStyle = {
  padding: '2rem',
};

const titleStyle = {
  textAlign: 'center',
  marginBottom: '2rem',
};

const cartItemStyle = {
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
  padding: '1rem',
  border: '1px solid #ddd',
  borderRadius: '8px',
  backgroundColor: '#fafafa',
};

const itemNameStyle = {
  flex: 2,
};

const priceStyle = {
  flex: 1,
};

const quantityStyle = {
  display: 'flex',
  alignItems: 'center',
  gap: '0.5rem',
  flex: 1,
};

const buttonStyle = {
  padding: '0.3rem 0.6rem',
  background: '#3498db',
  color: 'white',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
};

const removeButtonStyle = {
  ...buttonStyle,
  background: '#e74c3c',
};

const totalStyle = {
  textAlign: 'right',
  marginTop: '2rem',
};

function CartPage() {
  const { cart, removeFromCart, updateQuantity, getTotalPrice } = useCart();

  if (cart.length === 0) {
    return <p style={{ textAlign: 'center', marginTop: '2rem' }}>Your cart is empty.</p>;
  }

  return (
    <div style={containerStyle}>
      <h1 style={titleStyle}>Shopping Cart</h1>

      <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
        {cart.map(item => (
          <div key={item.id} style={cartItemStyle}>
            <div style={itemNameStyle}>{item.name}</div>
            <div style={priceStyle}>${item.price}</div>

            <div style={quantityStyle}>
              <button onClick={() => updateQuantity(item.id, item.quantity - 1)} style={buttonStyle}>âˆ’</button>
              <span>{item.quantity}</span>
              <button onClick={() => updateQuantity(item.id, item.quantity + 1)} style={buttonStyle}>+</button>
            </div>

            <div style={priceStyle}>${(item.price * item.quantity).toFixed(2)}</div>

            <button onClick={() => removeFromCart(item.id)} style={removeButtonStyle}>Remove</button>
          </div>
        ))}
      </div>

      <h3 style={totalStyle}>Total: ${getTotalPrice().toFixed(2)}</h3>
    </div>
  );
}

export default CartPage;
