import React from 'react';
import { useCart } from '../context/CartContext';

function CartPage() {
  const { cart, removeFromCart, updateQuantity, getTotalPrice } = useCart();

  if (cart.length === 0) {
    return <p style={{ textAlign: 'center', marginTop: '2rem' }}>Your cart is empty.</p>;
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Shopping Cart</h1>
      {cart.map(item => (
        <div key={item.id} style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1rem' }}>
          <div>{item.name}</div>
          <div>${item.price}</div>
          <div>
            <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</button>
            <span style={{ margin: '0 0.5rem' }}>{item.quantity}</span>
            <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
          </div>
          <div>${(item.price * item.quantity).toFixed(2)}</div>
          <button onClick={() => removeFromCart(item.id)} style={{ background: '#e74c3c', color: 'white' }}>Remove</button>
        </div>
      ))}
      <h3 style={{ textAlign: 'right' }}>Total: ${getTotalPrice().toFixed(2)}</h3>
    </div>
  );
}

export default CartPage;
