import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

const mockProducts = [
  { id: 1, name: 'Laptop', price: 999, category: 'Electronics', image: 'https://images.unsplash.com/photo-1496181133206-80ce9b88a853?w=300&h=200&fit=crop' },
  { id: 2, name: 'Phone', price: 599, category: 'Electronics', image: 'https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?w=300&h=200&fit=crop' },
  { id: 3, name: 'T-Shirt', price: 29, category: 'Clothing', image: 'https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?w=300&h=200&fit=crop' },
  { id: 4, name: 'Jeans', price: 79, category: 'Clothing', image: 'https://images.unsplash.com/photo-1542272604-787c3835535d?w=300&h=200&fit=crop' },
  { id: 5, name: 'Book', price: 19, category: 'Books', image: 'https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=300&h=200&fit=crop' },
  { id: 6, name: 'Headphones', price: 149, category: 'Electronics', image: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=300&h=200&fit=crop' }
];

function HomePage() {
  const { addToCart } = useCart();
  const [filters, setFilters] = useState({ search: '', category: 'All', maxPrice: '' });

  const filtered = mockProducts.filter(p => {
    const s = filters.search.toLowerCase();
    const matchSearch = p.name.toLowerCase().includes(s);
    const matchCat = filters.category === 'All' || p.category === filters.category;
    const matchPrice = !filters.maxPrice || p.price <= parseFloat(filters.maxPrice);
    return matchSearch && matchCat && matchPrice;
  });

  const categories = ['All', ...new Set(mockProducts.map(p => p.category))];

  return (
    <div style={{ padding: '0 2rem' }}>
      <h1 style={{ textAlign: 'center', color: '#2c3e50' }}>Product Catalog</h1>
      <div style={{ backgroundColor: '#ecf0f1', padding: '1rem', borderRadius: '8px', marginBottom: '2rem' }}>
        <input
          type="text"
          placeholder="Search"
          value={filters.search}
          onChange={e => setFilters(f => ({ ...f, search: e.target.value }))}
          style={{ padding: '0.5rem', marginRight: '1rem' }}
        />
        <select
          value={filters.category}
          onChange={e => setFilters(f => ({ ...f, category: e.target.value }))}
          style={{ padding: '0.5rem', marginRight: '1rem' }}
        >
          {categories.map(c => <option key={c}>{c}</option>)}
        </select>
        <input
          type="number"
          placeholder="Max Price"
          value={filters.maxPrice}
          onChange={e => setFilters(f => ({ ...f, maxPrice: e.target.value }))}
          style={{ padding: '0.5rem' }}
        />
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '1.5rem' }}>
        {filtered.map(product => (
          <div key={product.id} style={{
            border: '1px solid #ddd', borderRadius: '8px',
            padding: '1rem', textAlign: 'center', background: 'white'
          }}>
            <img src={product.image} alt={product.name} style={{ width: '100%', height: '180px', objectFit: 'cover', borderRadius: '4px' }} />
            <h3>{product.name}</h3>
            <p>{product.category}</p>
            <p><strong>${product.price}</strong></p>
            <button onClick={() => addToCart(product)} style={{ padding: '0.5rem 1rem', background: '#3498db', color: 'white', border: 'none', borderRadius: '4px' }}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
