import React from 'react';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';

const mockProducts = [
  { id: 1, name: 'Laptop', price: 999, category: 'Electronics', description: 'High-performance laptop.', image: 'https://images.unsplash.com/photo-1496181133206-80ce9b88a853?w=300&h=200&fit=crop' },
  { id: 2, name: 'Phone', price: 599, category: 'Electronics', description: 'Latest smartphone.', image: 'https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?w=300&h=200&fit=crop' },
  { id: 3, name: 'T-Shirt', price: 29, category: 'Clothing', description: 'Comfortable cotton shirt.', image: 'https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?w=300&h=200&fit=crop' },
  { id: 4, name: 'Jeans', price: 79, category: 'Clothing', description: 'Stylish denim.', image: 'https://images.unsplash.com/photo-1542272604-787c3835535d?w=300&h=200&fit=crop' },
  { id: 5, name: 'Book', price: 19, category: 'Books', description: 'Informative book.', image: 'https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=300&h=200&fit=crop' },
  { id: 6, name: 'Headphones', price: 149, category: 'Electronics', description: 'Noise-cancelling.', image: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=300&h=200&fit=crop' }
];

function HomePage({ filters }) {
  const { addToCart } = useCart();

  const filtered = mockProducts.filter(p => {
    const matchSearch = p.name.toLowerCase().includes(filters.search.toLowerCase());
    const matchCat = filters.category === 'All' || p.category === filters.category;
    const matchPrice = !filters.maxPrice || p.price <= parseFloat(filters.maxPrice);
    return matchSearch && matchCat && matchPrice;
  });

  return (
    <div style={{ padding: '0 2rem' }}>
      <h1 style={{ textAlign: 'center' }}>Product Catalog</h1>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '1.5rem' }}>
        {filtered.map(product => (
          <div key={product.id} style={{ border: '1px solid #ccc', padding: '1rem', borderRadius: '6px', background: 'white' }}>
            <Link to={`/product/${product.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
              <img src={product.image} alt={product.name} style={{ width: '100%', height: '180px', objectFit: 'cover' }} />
              <h3>{product.name}</h3>
              <p>{product.category}</p>
              <p>${product.price}</p>
            </Link>
            <button onClick={() => addToCart(product)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
